import React, { useState } from 'react';
import './loginButton.css';

const LoginForm = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [errorMessage, setErrorMessage] = useState('');




    const handleSubmit = async e => {
        e.preventDefault();

        try {
            const response = await fetch('http://localhost:5000/login', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ username, password }),
            });

            const data = await response.json();
            console.log(data); // Handle the response data here
        } catch (error) {
            console.error('Error:', error);
        }
    };

    const handleLogin = () => {
        // Perform any additional logic before logging in
        // For example, form validation

        // Redirect to the main page
        // Replace "window.location.href" with your preferred method of navigation
        window.location.href = '../mainPage';
    };

    return (
        <div className='modal'>
            <h2>Please enter username and password</h2>
            <form className='loginForm' onSubmit={handleSubmit}>
                <input
                    type="text"
                    placeholder="Username"
                    style={{ width: '200px', height: '30px', textAlign: 'center' }}
                    value={username}
                    onChange={e => setUsername(e.target.value)}
                />
                <input
                    type="password"
                    placeholder="Password"
                    style={{ width: '200px', height: '30px', textAlign: 'center' }}
                    value={password}
                    onChange={e => setPassword(e.target.value)}
                />
                <input type="button" className='logbtn' onClick={handleLogin} value='Login' />
            </form>
        </div>

    );
};

export default LoginForm;
