import React, { useState } from 'react';
import AddingForm from './AddingForm';
import './addButton.css';

const AddButton = () => {
  const [showForm, setShowForm] = useState(false);

  const handleButtonClick = () => {
    setShowForm(true);
  };

  return (
    <div>
      {showForm ? (
        <AddingForm />
      ) : (
        <input
          type={'button'}
          value={'Lis채채 laite'}
          onClick={handleButtonClick}
          className='' />
      )}
    </div>
  )
}

export default AddButton
