import React, { useState } from 'react';
import Modal from 'react-modal';
import './loginButton.css';
import Axios from 'axios';

const LoginButton = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleOpenModal = () => {
    setIsModalOpen(true);
  };

  const handleCloseModal = () => {
    setIsModalOpen(false);
  };

  const handleLogin = () => {
    Axios.post("http://localhost:3001/api/login", {
      username: username,
      password: password
    })
      .then((response) => {
        // Handle successful login
        console.log(response.data); // Assuming the response contains user data or authentication token
        setUsername('');
        setPassword('');
        setIsModalOpen(false);
      })
      .catch((error) => {
        // Handle login error
        console.error(error);
      });
  };

  return (
    <>
      <button onClick={handleOpenModal} className='logbtn'>Kirjaudu sis채채n</button>
        <Modal isOpen={isModalOpen} onRequestClose={handleCloseModal} style={{back}}>
          <h2>Kirjaudu sis채채n</h2>
          <form onSubmit={handleLogin} className='loginForm'>
            <label htmlFor="username">Username:</label>
            <input
              name="username"
              type="text"
              autoComplete="username"
              required
              id="username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
            <label htmlFor="password">Password:</label>
            <input
              name="password"
              autoComplete="current-password"
              required
              id="password"
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <button type="submit" className='submitbtn'>Submit</button>
          </form>
        </Modal>
    </>
  );
};

export default LoginButton;
